$(window).scroll(function() {
    var scroll = $(window).scrollTop();

    if (scroll >= 30) {
        $("header").addClass("header-scrolled");
        $("header").removeClass("header-unscrolled");
    } else {
        $("header").removeClass("header-scrolled");
        $("header").addClass("header-unscrolled");
    }
});

